import React, {useState, useEffect} from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { getPostsById } from "../redux/actions/actions";
import { useRouter } from "next/router";
// import styled from "styled-components";
// import { InitialStateI } from "../redux/reducers/reducer";
ipm

export default () => {
    const [comments, setComments] = useState<CommentI>([]);

    const dispatch = useDispatch();
    const router = useRouter();
    const { postId } = router.query;
  
    useEffect(() => {
      dispatch(getPostsById);
      axios
        .get(`https://simple-blog-api.crew.red/posts/${postId}?_embed=comments`)
        .then((data) => {
          console.log("data.data :>> ", data.data.comments);
          setComments(data.data.comments);
        })
        .catch((err) => {
          console.log("Error!", err);
        });
    }, [postId]);
  return (
    <ul>
      {comments.map((comment) => {(
          <li key={comment.id} className="list-group-item">
            {comment.body}
          </li>
        );
      })}
    </ul>
  );
};
