import { POSTS_FAIL, POSTS_LOADING, POSTS_SUCCESS } from '../types/types';

interface InitialStateI {
    posts: any
}

const initialState: InitialStateI = {
    posts: [],
    isLoading
  };

  const reducer = (state = postInitialState, action) => {
    switch (action.type) {
        case GET_DATA:
            return { ...state, isLoading: !state.isLoading };

        case SUCCESS_DATA:
            return { ...state, imgs: [...action.payload] };

        case ERROR_DATA:
            return state;

        default:
            return state;
    }
};
