import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";

const IDS = {
  MESSEGE: "comment",
};

export default () => {
  const [state, setState] = useState({
    [IDS.MESSEGE]: "",
  });

  const onChange = (event) => {
    const { id, value } = event.target;

    setState((currentState) => ({
      ...currentState,
      [id]: value,
    }));
  };

  return (
    <form>
      <Input
        id={IDS.MESSEGE}
        value={state[IDS.MESSEGE]}
        onChange={onChange}
        placeholder="Comment..."
      />
    </form>
  );
};

const Input = styled.input`
  padding-left: 25px;
  font-size: 16px;
  width: 675px;
  height: 45px;
  margin-top: 40px;
  border: none;
  border-radius: 10px;
  box-shadow: inset 0 0 7px rgba(0, 0, 0, 0.2);
`;
