import axios from 'axios';
import { POSTS_FAIL, POSTS_LOADING, POSTS_SUCCESS } from '../types/types';

const postsLoading = () => ({
  type: POSTS_LOADING,
});

const successData = (data) => ({
  type: POSTS_SUCCESS,
  payload: data,
});

const errorData = (error) => ({
  type: POSTS_FAIL,
  payload: error,
});




const getPosts = () => (dispatch) => {
  postsLoading();
  axios
    .get('https://simple-blog-api.crew.red/posts')
    .then((res) => dispatch(successData(res.data)))
    .catch((error) => dispatch(errorData(error)))
    .finally(() => postsLoading());
};

// export function getPost(id: string | string[], dispatch: Function) {
//     if (id === undefined) return;
//     axios(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`)
//         .then(res => dispatch({ type: "GETPOST", post: res.data }));
// };

const getPostsComment = (id: string | string[]) => (dispatch: Function) => {
  postsLoading();
  axios
    .get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`)
    .then((res) => dispatch(successData(res.data)))
    .catch((error) => dispatch(errorData(error)))
    .finally(() => postsLoading());
};

export { getPosts, getPostsComment };
