import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { getPostsById } from "../redux/actions/actions";
import { useRouter } from "next/router";
import styled from "styled-components";
// import { InitialStateI } from "../redux/reducers/reducer";
import { CommentI } from "../redux/reducers/reducer";

export default () => {
  const [commentsArr, setCommentsArr] = useState([]);

  const dispatch = useDispatch();
  const router = useRouter();
  const { postId } = router.query;

  useEffect(() => {
    dispatch(getPostsById);
    // console.log('postId :>> ', postId);
    axios
      .get(`https://simple-blog-api.crew.red/posts/${postId}?_embed=comments`)
      .then((data) => {
        setCommentsArr(data.data.comments);
      })
      .catch((err) => {
        console.log("Error!", err);
      });
  }, [postId]);
  return (
    <ul>
      {commentsArr.map((comment) => (
        <Item key={comment.id}>
          <p>{comment.body}</p>
        </Item>
      ))}
    </ul>
  );
};

const Item = styled.li`
  width: 100%;
  height: 60px;
  display: flex;
  align-items: center;
  width: 700px;
  margin-bottom: 15px;
  margin-top: 15px;
  border-radius: 5px;
  background: #fff;
  box-shadow: 0px 4px 25px 0px rgba(0,0,0,0.05);
`;
