import {
  PostType,
  PostsDispatchTypes,
  POSTS_SUCCESS,
  POSTS_LOADING,
  POSTS_FAIL,
} from "../types/BlogActionType";

interface DefaultStateI {
  loading: boolean;
  post?: PostType;
}

const defaultState: DefaultStateI = {
  loading: false,
};

const blogReducer = (
  state: DefaultStateI = defaultState,
  action: PostsDispatchTypes
): DefaultStateI => {
  switch (action.type) {
    case POSTS_FAIL:
      return {
        loading: false,
      };
    case POSTS_LOADING: 
      return {
        loading: true,
      };
    case POSTS_SUCCESS: 
        return {
        loading: false,
        post: action.payload 
      }
    de
  }
};

export default blogReducer;
