import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { getPostsById } from "../redux/actions/actions";
import { useRouter } from "next/router";
import styled from "styled-components";
// import { InitialStateI } from "../redux/reducers/reducer";
import { CommentI } from "../redux/reducers/reducer";

export default () => {
  const [commentsArr, setCommentsArr] = useState([]);

  const dispatch = useDispatch();
  const router = useRouter();
  const { postId } = router.query;

  useEffect(() => {
    dispatch(getPostsById);
    // console.log('postId :>> ', postId);
    axios
      .get(`https://simple-blog-api.crew.red/posts/${postId}?_embed=comments`)
      .then((data) => {
        setCommentsArr(data.data.comments);
      })
      .catch((err) => {
        console.log("Error!", err);
      });
  }, [postId]);
  return (
    <ul>
      {commentsArr.map((comment) => (
        <Item key={comment.id}>
          <UserName>User</UserName>
          <Desc>{comment.body}</Desc>
        </Item>
      ))}
    </ul>
  );
};

const Item = styled.li`
  width: 100%;
  height: 80px;
  display: flex;
  justify-content: center;
  flex-direction: column;
  width: 700px;
  margin-bottom: 15px;
  margin-top: 15px;
  border-radius: 5px;
  background: #f7f7f7;
`;

const Desc = styled.p`
  padding-left: 40px;
`;

const UserName = styled.p`
  padding-left: 40px;
  margin-bottom: 10px;
  color: #738a;
`;

