import {
  POSTS_FAIL,
  POSTS_LOADING,
  POSTS_SUCCESS,
  POST_SUCCESS,
} from "../types/types";
export interface post {
  id: number;
  title: string;
  body: string;
  comments?: Array<comment>;
}
export interface comment {
  id: number;
  postId: number;
  body: string;
}
export interface InitialStateI {
  posts: Array<post>;
  isLoading: boolean;
  coments: [];
  post?: post;
}

export interface state {
  posts: Array<post>,
  post?: post,
  title: string,
  body: string,
  comment: string,
  edit: boolean
};

const initialState: InitialStateI = {
  posts: [],
  isLoading: false,
  coments: [],
};

const reducer = (state: InitialStateI = initialState, action) => {
  switch (action.type) {
    case POSTS_LOADING:
      return { ...state, isLoading: !state.isLoading };

    case POSTS_SUCCESS:
      return { ...state, posts: [...action.payload] };

    case POSTS_FAIL:
      return state;

    case POST_SUCCESS:
      return { ...state, post: action.post };

    default:
      return state;
  }
};

export default reducer;
