import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { connect, useDispatch } from 'react-redux';
import { getPosts, getPostsComment } from '../redux/actions/actions';
import axios from 'axios';

const Home: React.FC = () => {
  const [posts, setPosts] = useState([]);
  const [comment, setComment] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  //Get posts
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPosts);
    axios
      .get('https://simple-blog-api.crew.red/posts')
      .then((data) => {
        setPosts(data.data);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log('Error!', err);
      });
  }, []);

  useEffect(() => {
    dispatch(getPostsComment);
    axios
      .get(`https://simple-blog-api.crew.red/posts/${27}?_embed=comments`)
      .then((data) => {
        console.log('data.data :>> ', data.data);
        // setComment(data.data);
      })
      .catch((err) => {
        console.log('Error!', err);
      });
  }, []);
  return (
    <>
      <List>
        {posts.map((post) => (
          <Item key={post.id}>
            <p>{post.title}</p>
            <p>{post.body}</p>
            <p>{post.id}</p>
          </Item>
        ))}
      </List>
    </>
  );
};

const List = styled.ul`
  display: flex;
  list-style: none;
`;

const Item = styled.li`
  width: 300px;
  heigth: 150px;
  border-radius: 10px;
  box-shadow: 0px 4px 25px 0px rgba(0,0,0,0.75);
`

export default connect(null, { getPosts, getPostsComment })(Home);
