import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { getPostsById } from "../redux/actions/actions";
import { useRouter } from "next/router";
// import styled from "styled-components";
// import { InitialStateI } from "../redux/reducers/reducer";
import { CommentI } from "../redux/reducers/reducer";

export default () => {
  const [ggggg, setComments] = useState([]);

  const dispatch = useDispatch();
  const router = useRouter();
  const { postId } = router.query;

  useEffect(() => {
    dispatch(getPostsById);
    axios
      .get(`https://simple-blog-api.crew.red/posts/${33}?_embed=comments`)
      .then((data) => {
        console.log("data.data.comments :>> ", data.data);
        setComments(data.data.comments);
      })
      .catch((err) => {
        console.log("Error!", err);
      });
  }, [postId]);
  return (
    <ul>
      {comments.map((comment) => (
        <li key={comment.id}>
          <p>{comment.body}</p>
        </li>
      ))}
    </ul>
  );
};
