import { PostType } from "../types/BlogActionType";

interface DefaultStateI {
    loading: boolean
    post: PostType
}

const defaultState: DefaultStateI = {
    loading: false
}

const blogReducer = (state: DefaultStateI = defaultState, action: any): DefaultStateI => {
    return state
}

export default blogReducer;
